func ExampleString() {
	var r, g, b uint8
	// pick a color
	r, g, b = 252, 255, 43
	// choose a word
	word := "=)"
	// colorize it!
	coloredWord := rgbterm.String(word, r, g, b)

	fmt.Println("Oh!", coloredWord, "hello!")

	// Output:
	// Oh! [38;5;165m=)[0;00m hello!
}
